import { useState } from "react";


const CakeForm = ({info, setInfo}) => {

    const [cakeName, setCakeName] = useState("");
    const [ingredients, setIngredients] = useState("")
    const [price, setPrice] = useState("");
    const [rating, setRating] = useState("");
    const [error, setError] = useState("");

    const handleValidation = () => {

        let errorMessage = "";

        if(info.find((cakeName) => info.cakeName === cakeName)) {
            errorMessage = "This cake name already exists!"
        }

        if(info.find((ingredients) => info.ingredients === ingredients)) {
            errorMessage = "This list of ingredients already exists!"
        }

        if(cakeName === "" || ingredients === "" || price === "" || rating === "" ) {
            errorMessage = "Please fill in all fields!"
        }

        setError(errorMessage);

        return errorMessage !== "";
    }

    const handleFormSubmit = (e) => {
        e.preventDefault();

        if(!handleValidation()) {

            const updateCakes = [...info];

            const newCake = {
            cakeName : cakeName,
            ingredients : ingredients,
            price : price,
            rating : rating
        }

        updateCakes.push(newCake);
        setInfo(updateCakes);
    }
}

    return (
        <>
        <h2>Add a new cake: </h2>
        <form onSubmit = {handleFormSubmit} >
            <input 
            type = "text"
            name = "cake name"
            placeholder = "Cake's name"
            value = {cakeName}
            onChange = { (e) => setCakeName(e.target.value)} />

            <input 
            type = "text"
            name = "ingredients"
            placeholder = "Ingredients"
            value = {ingredients}
            onChange = { (e) => setIngredients(e.target.value)} />

            <input 
            type = "text"
            name = "price"
            placeholder = "Price"
            value = {price}
            onChange = { (e) => setPrice(e.target.value)} />

            <input 
            type = "text"
            name = "rating"
            placeholder = "Rating"
            value = {rating}
            onChange = { (e) => setRating(e.target.value)} />
            <input type =  "submit" value = "Submit" />

        </form>




        </>
    )

}

export default CakeForm;
